	function TestScenario(strInfo,strTable,strPrefix)
	{

		Log('\nTest scenario "'+strInfo+'" on table "'+strTable+'"\n');
		
		var strIncident = testdata.CreateIncident('TEST_'+strTable,'OPENER','CALLER','ALPHA_MEMBER');
		Log('\nCreateIncident returned: '+RenderValue(strIncident)+'\n');
		//Log(RenderValue(testdata.GetAllIncidents()));
		
		TestCase_RecordQuery(strUserSentinel,strTable,strPrefix,strIncident,true);
		TestCase_RecordQuery(strUserSpectator,strTable,strPrefix,strIncident,false);

		TestCase_RecordQuery(strUserCaller,strTable,strPrefix,strIncident,true);
		TestCase_RecordQuery(strUserOpener,strTable,strPrefix,strIncident,true);

		TestCase_RecordQuery(strUserAlphaMember,strTable,strPrefix,strIncident,true);
		TestCase_RecordQuery(strUserAlphaFulfiller,strTable,strPrefix,strIncident,true);
		TestCase_RecordQuery(strUserAlphaSupervisor,strTable,strPrefix,strIncident,true);

		TestCase_RecordQuery(strUserBetaMember,strTable,strPrefix,strIncident,false);
		TestCase_RecordQuery(strUserBetaFulfiller,strTable,strPrefix,strIncident,false);
		TestCase_RecordQuery(strUserBetaSupervisor,strTable,strPrefix,strIncident,false);


		Log('\nMove incident to member team in beta silo\n');
		RecordSetValue('incident',strIncident,'assignment_group',strGroupBetaMember);

		TestCase_RecordQuery(strUserSentinel,strTable,strPrefix,strIncident,true);
		TestCase_RecordQuery(strUserSpectator,strTable,strPrefix,strIncident,false);

		TestCase_RecordQuery(strUserCaller,strTable,strPrefix,strIncident,true);
		TestCase_RecordQuery(strUserOpener,strTable,strPrefix,strIncident,true);

		TestCase_RecordQuery(strUserAlphaMember,strTable,strPrefix,strIncident,true);
		TestCase_RecordQuery(strUserAlphaFulfiller,strTable,strPrefix,strIncident,true);
		TestCase_RecordQuery(strUserAlphaSupervisor,strTable,strPrefix,strIncident,true);

		TestCase_RecordQuery(strUserBetaMember,strTable,strPrefix,strIncident,true);
		TestCase_RecordQuery(strUserBetaFulfiller,strTable,strPrefix,strIncident,false);
		TestCase_RecordQuery(strUserBetaSupervisor,strTable,strPrefix,strIncident,true);
		
		Log('\n\n');

	}

	Log('4. The actual test');
	
	TestScenario('Original table','incident','');

	// The following test rely on timely creation of task_sla records:
	// com.snc.sla.engine.async must be false, otherwise the task_sla record will be created later and some of the test cases would fail.
	if (gs.getProperty('com.snc.sla.engine.async') == 'false')
	{
		TestScenario('OOTB database view without fields','incident_sla','inc_');
		TestScenario('Custom database view with multiple tables WITH fields',testdata.GetDatabaseViewName(strDbViewHandleTest1),'inc_');
	}
	TestScenario('Custom database view with a single table WITH fields',testdata.GetDatabaseViewName(strDbViewHandleTest2),'inc_');

	TestScenario('Custom database view with a left join on the same table',testdata.GetDatabaseViewName(strDbViewHandleLeftJoin1),'incident1_');

	
	Log(RenderValue(testdata.GetAllData(),true));
		
	stepResult.setOutputMessage(strLog);
	return bResult;
	
})(outputs, steps, stepResult, assertEqual);
