sys_script_include.script:
function IsRecordComplete(record)
{
	var RenderValue = x_snc_devtools.RenderValue;
	var IsValidRecord = x_snc_devtools.IsValidRecord;

	function Log(strText)
	{
		x_snc_datasilos.Log('IsRecordComplete: '+record.number+': '+strText);
	}
	function Debug(strText)
	{
		x_snc_datasilos.Debug('IsRecordComplete: '+record.number+': '+strText);
	}	

	if (IsValidRecord(record) == false)
	{
		return false;
	}

	strScope = GetScopeFromRecord(record);

	var bComplete = true;
	if (record[strScope+'_global'] == undefined)
	{
		return false;
	}
	if (record[strScope+'_ghost'] == undefined)
	{
		return false;
	}	
	if (record[strScope+'_group_whitelist'] == undefined)
	{
		return false;
	}
	if (record[strScope+'_supervisor_whitelist'] == undefined)
	{
		return false;
	}
	return bComplete;
}
